INSERT INTO parameter(name, label, description, enabled, fixed_value, ordering, model_id) VALUES ('q', 'q', 'The order of the ARCH terms (past squared error terms or lagged squared residuals from the mean equation) in the GARCH(p, q) model.', false, false, 2, (select id from Model where name='nixtla_garch_forecaster'));
